import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
  container: {
    padding: 10,
  },
  messageBox: {
    marginRight: 15,
    padding: 10,
  },
  timestamp: {
    fontSize: 12,
    color: '#ccc',
    justifyContent: 'flex-end'
  }
});

export default styles;
